import {ParametricIcon} from "/node_modules/parametric-icon/index.js"
import {template} from "../templates/CustomNoise.js"

/**
 * @class CustomNoise
 * @extends ParametricIcon
 * @classdesc 
 * 
 * A parameterizable icon, use is with the icon-custom-noise html tag. 
 * All the parameter of this class can also be passed through html attribute.
 * 
 * @property {string} fill an image parameter.
 * @property {string} stroke an image parameter.
 * @property {string} text an image parameter.
 * @property {number} strength How much of the sound effect on the front is visible, on a range from 0 to 100
 */
class CustomNoise extends ParametricIcon {

	connectedCallback(){
		this.setStyle(":host{display:block}")
		super.connectedCallback()
	}
	static get shadowMode(){
		return "open"
	}
	static get imageParameters() {
		return ["fill","stroke","text","strength"]
	}
	static get serialize() {
		let s =  this.standardSerial
		s.strength = "number"
		return s
	}
	static get template(){
		return template
	}
}
export default CustomNoise
