import {ParametricIcon} from "/node_modules/parametric-icon/index.js"
import {template} from "../templates/Light.js"

/**
 * @extends ParametricIcon
 * @classdesc 
 * A parameterizable icon, use is with the icon-light html tag. All the
 * parameter of this class can also be passed through html attribute.
 *
 * @property   {string}  raylength  Length of the ray (0 to 1)
 * @property   {string}  sivalue    The value of the measure, can display nicely
 *                                  decimal value and make use of abbreviated
 *                                  metric prefix. For example 12.33M will be
 *                                  displayed as 12.33 MLux.
 *
 * @class      Light
 */
class Light extends ParametricIcon {

	connectedCallback(){
		this.setStyle(":host{display:block}")
		super.connectedCallback()
	}
	static get shadowMode(){
		return "open"
	}
	static get imageParameters() {
		return ["raylength","sivalue"]
	}
	static get serialize() {
		return this.standardSerial
	}
	static get template(){
		return template
	}
}
export default Light
